    def search_type(self):
        _translate = QtCore.QCoreApplication.translate
        dsn_tns = cx_Oracle.makedsn('localhost', '1521', service_name='orcl') 
        conn = cx_Oracle.connect(user='test', password='111', dsn=dsn_tns) 

        mycursor = conn.cursor()
        mycursor.execute(str(open('7.2_.txt').read().split(';')[0]).encode().decode('utf8').encode('cp1251').decode('utf8')) 
        result = mycursor.fetchall()
        #print(result)
        row_count = mycursor.rowcount

        if row_count == 0:
            self.label.setText(_translate("MainWindow", "Не найдено ни одного транспорта"))
        else:
            self.label.setText(_translate("MainWindow", "Найдено " + str(row_count) + " разного тарнспорта. Выберете один из них."))
            for row_number, row_data in enumerate(result):
                #self.tableWidget.insertRow(row_number)
                data_str = ""
                for column_number, data in enumerate(row_data):
                    data_str += str(data) + " - "
                if self.comboBox.count() < int(row_count) + 1:
                    self.comboBox.addItem("")
                self.comboBox.setItemText(row_number + 1, _translate("MainWindow", data_str.rstrip(" - ")))
        mycursor.close()
        conn.close()

    def select_search(self):
        dsn_tns = cx_Oracle.makedsn('localhost', '1521', service_name='orcl') 
        conn = cx_Oracle.connect(user='test', password='111', dsn=dsn_tns) 
        mycursor = conn.cursor()
        
        if self.comboBox.currentText() == "Выберете нужную технику":
            self.search_type()
        else:
            str_input = self.comboBox.currentText().split(" - ")
            #print(str_input)
            mycursor.execute(str(open('7.2__.txt').read().split(';')[0]).encode().decode('utf8').encode('cp1251').decode('utf8').format(str_input[0], str_input[1], str_input[2])) 
            id = mycursor.fetchall()[0][0]

            mycursor.execute(str(open('7.2.txt').read().split(';')[0]).encode().decode('utf8').encode('cp1251').decode('utf8').format(id)) 
            result = mycursor.fetchall()

            if mycursor.rowcount > 0:
                self.label.setText(QtCore.QCoreApplication.translate("MainWindow", "Найдено по этому авто: " + self.comboBox.currentText()))
            else:
                self.label.setText(QtCore.QCoreApplication.translate("MainWindow", "Ничего не найдено по этому авто: " + self.comboBox.currentText()))
            self.tableWidget.setRowCount(0)

            for row_number, row_data in enumerate(result):
                self.tableWidget.insertRow(row_number)

                for column_number, data in enumerate(row_data):
                    data_str = str(data)
                    if data_str == "None":
                        data_str = ""
                    self.tableWidget.setItem(row_number, column_number, QTableWidgetItem(data_str))

        mycursor.close()
        conn.close()

